mapper.select:
SELECT id,name,`time`,`date`,consume
FROM play p LEFT JOIN
sport s ON
p.sport_id = s.id
WHERE p.account_id = #{param1}
AND `date` = #{param2}

mapper.delete:
DELETE FROM play
WHERE account_id = #{param1}
AND sport_id = #{param2}
AND `date` = #{param3}

mapper.update:
UPDATE play
SET `time` = #{param3}
WHERE account_id = #{param1}
AND sport_id = #{param2}
AND `date` = #{param4,jdbcType=DATE}

UPDATE play
SET `time` = #{time}
WHERE account_id = #{account_id}
AND sport_id = #{sport.id}
AND `date` = #{date,jdbcType=DATE}
